import api from "../utils/api";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import {useEffect, useState} from "react";
import ImagePopup from "./ImagePopup";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import {CurrentUserContext, userContext} from '../contexts/CurrentUserContext';

function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
    const [isDeleteCardPopupOpen, setIsDeleteCardPopupOpen] = useState(false);
    const [selectedCard, setSelectedCard] = useState({})
    const [currentUser, setCurrentUser] = useState(userContext)
    const [cards, setCards] = useState([])

    useEffect(() => {
        Promise.all([api.getUserInfo(), api.getCards()])
            .then(([userData, cardsData]) => {
                setCurrentUser(userData)
                setCards(cardsData)
            })
            .catch((err) => {
                alert(err)
            })
    }, [])

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true)
    }

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true)
    }

    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true)
    }

    function handleDeleteCardClick() {
        setIsDeleteCardPopupOpen(true)
    }

    function closeAllPopups() {
        setIsEditProfilePopupOpen(false)
        setIsAddPlacePopupOpen(false)
        setIsEditAvatarPopupOpen(false)
        setIsDeleteCardPopupOpen(false)
        setSelectedCard({})
    }

    function handleCardClick(selectedCard) {
        setSelectedCard(selectedCard)
    }

    // функция лайка карточки
    function handleCardLike(card) {
        const isLiked = card.likes.some(item => item._id === currentUser._id);
        if (isLiked) {
            api.deleteLike(card._id)
                .then((res) => {
                    setCards((cards) =>
                        cards.map((item) => (item._id === card._id ? res : item)))
                })
                .catch((err) => {
                    alert(err)
                })
        } else {
            api.addLike(card._id)
                .then((res) => {
                    setCards((cards) =>
                        cards.map((item) => (item._id === card._id ? res : item)))
                })
                .catch((err) => {
                    alert(err)
                })
        }
    }

    // функция удаления карточки
    function handleCardDelete (card) {
        api.deleteCard(card._id)
            .then(
                setCards((cards) =>
                    cards.filter((item) => (item._id !== card._id))
                )
            )
            .catch((err) => {
                alert(err)
            })
    }

    function handleUpdateUser ({name, description}) {
        api.editProfile(name, description)
            .then((res) => setCurrentUser(res))
            .catch((err) => {
                alert(err)
            })
        closeAllPopups()
    }

    function handleUpdateAvatar ({avatar}) {
        api.editAvatar(avatar)
            .then((res) => setCurrentUser(res))
            .catch((err) => {
                alert(err)
            })
        closeAllPopups()
    }

    function handleAddPlaceSubmit ({name, link}) {
        api.sendCard(name, link)
            .then((res) => console.log(res))
    }

    return (
        <CurrentUserContext.Provider value={currentUser}>
            <Header/>
            <Main
                openEditProfile={handleEditProfileClick}
                openAddPlace={handleAddPlaceClick}
                openAvatar={handleEditAvatarClick}
                openDeleteCard={handleDeleteCardClick}
                onCardClick={handleCardClick}
                onCardLike={handleCardLike}
                onCardDelete={handleCardDelete}
                cards={cards}
            />
            <ImagePopup card={selectedCard} onClose={closeAllPopups}/>
            <EditProfilePopup
                isOpen={isEditProfilePopupOpen}
                onClose={closeAllPopups}
                onUpdateUser={handleUpdateUser}
            />
            <EditAvatarPopup
                isOpen={isEditAvatarPopupOpen}
                onClose={closeAllPopups}
                onUpdateAvatar={handleUpdateAvatar}
            />
            <AddPlacePopup
                isOpen={isAddPlacePopupOpen}
                onClose={closeAllPopups}
                onAddCard={handleAddPlaceSubmit}
            />
            {/* DELETE CARD */}
            <PopupWithForm
                name={"edit-avatar"}
                title={"Вы уверены?"}
                isOpen={isDeleteCardPopupOpen}
                onClose={closeAllPopups}
                buttonText={"Да"}
            >
            </PopupWithForm>
            <Footer/>
        </CurrentUserContext.Provider>
    )
}

export default App;
