import {useState} from "react";
import PopupWithForm from "./PopupWithForm";
import {userContext} from "../contexts/CurrentUserContext";

function  EditProfilePopup (props) {
    const [personValue, setPersonValue] = useState(userContext);
    const [aboutValue, setAboutValue] = useState(userContext.about);

    function handleChangePersonValue(e) {
        setPersonValue(e.target.value);
    }

    function handleChangeAboutValue(e) {
        setAboutValue(e.target.value);
    }

    return (
        <PopupWithForm
            name={"edit-profile"}
            title={"Редактировать профиль"}
            buttonText={"Cохранить"}
            isOpen={props.isOpen}
            onClose={props.onClose}
        >
            <input
                type="text"
                className="popup__input popup__input_person"
                id="person"
                name="person"
                onChange={handleChangePersonValue}
                value={personValue}
                placeholder="Имя"
                required
                minLength="2"
                maxLength="40"
            />
            <span
                id="person-errors"
                className="popup__error popup__error_visible">
            </span>
            <input
                type="text"
                className="popup__input popup__input_about-me"
                id="about-me"
                name="aboutMe"
                onChange={handleChangeAboutValue}
                value={aboutValue}
                placeholder="Деятельность"
                required
                minLength="2"
                maxLength="200"
            />
            <span
                id="about-me-errors"
                className="popup__error popup__error_visible">
            </span>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
