import React from "react";
import api from "../utils/api";
import {useEffect, useState} from "react";
import Card from "./Card"

// import ImagePopup from "./ImagePopup";

function Main ({openEditProfile, openAddPlace, openAvatar}) {
    const [userAvatar, setUserAvatar] = useState('https://goo.su/5KqwD')
    const [userName, setUserName] = useState('Имя')
    const [userDescription, setUserDescription] = useState('Деятельность')
    // const [initialCards, setInitialCards] = useState([])

    useEffect(() => {
        Promise.all([api.getProfile(), api.getCards()])
        .then(([res, initialCards]) => {
            setUserAvatar(res.avatar)
            changeUserName(res.name)
            changeUserDescription(res.about)

            // setInitialCards(initialCards)

        })
        .catch((err) => {
            console.log(err)
        })
    })



    function changeUserName (name) {
        setUserName(name)
    }

    function changeUserDescription (description) {
        setUserDescription(description)
    }



    return (
        <main className="content">
            <section className="profile">
                <div className="profile__avatar-wrapper">
                    <button
                        type="button"
                        className="profile__avatar-button"
                        onClick={openAvatar}
                    />
                    <div
                        className="profile__avatar"
                        style={{ backgroundImage: `url(${userAvatar})` }}
                    >
                    </div>
                </div>
                <div className="profile__info">
                    <h1 className="profile__person">{userName}</h1>
                    <p className="profile__about-me">{userDescription}</p>
                    <button
                        type="button"
                        className="profile__edit-button"
                        aria-label="Редактировать профиль"
                        onClick={openEditProfile}
                        title={'Редактировать профиль'}
                        name={'edit-profile'}
                    />
                </div>
                <button
                    onClick={openAddPlace}
                    className="profile__add-button"
                    type="button"
                />
            </section>
            <section className="elements">
                <Card card={initialCards}/>
            </section>
        </main>
    )
}

export default Main;
