import React from "react";

function Card({openDeleteCard, card, currentUser, handleCardClick}) {

    // Проверка айди пользователя и владельца карточки
    const isOwn = card.owner._id === currentUser._id;
    const cardDeleteButtonClassName = (
        `card__delete-button ${isOwn ? 'card__delete-button_visible' : 'card__delete-button_hidden'}`
    );

    function handleClick() {
        handleCardClick({name: card.name, image: card.link})
    }

    return (
        <article
            className="element"
        >
            <div className="element__contain">
                <button
                    className={`element__trash ${cardDeleteButtonClassName}`}
                    type="button"
                    onClick={openDeleteCard}
                >
                </button>
                <img
                    className="element__image"
                    onClick={handleClick}
                    alt={card.title}
                    src={card.link}
                />
            </div>
            <div className="element__description">
                <h2 className="element__title">{card.name}</h2>
                <div className="element__like-wrapper">
                    <button type="button" className="element__like">
                    </button>
                    <span className="element__like-value">{card.likes.length}</span>
                </div>
            </div>
        </article>
    )
}

export default Card;
