import React from "react";
import PopupWithForm from "./PopupWithForm";

function Main () {
    const [] = React.useState();

    function handleEditProfileClick(props) {
        console.log(props.type)

        PopupWithForm(
            {
                name: props.name,

            }
        )



    }

    function handleAddPlaceClick() {
        const popup = document.querySelector('.popup_type_add-place')
        popup.classList.add('popup_opened')
    }

    function handleEditAvatarClick() {
        const popup = document.querySelector('.popup_type_edit-avatar')
        popup.classList.add('popup_opened')
    }

    return (
        <main className="content">
            <section className="profile">
                <div className="profile__avatar-wrapper">
                    <button
                        type="button"
                        className="profile__avatar-button"
                        onClick={handleEditAvatarClick}
                    />
                    <img className="profile__avatar" alt="Аватар"
                         src="https://спортсекции18.рф/wp-content/uploads/2020/09/placeholder-man.png"
                    />
                </div>
                <div className="profile__info">
                    <h1 className="profile__person">Имя</h1>
                    <p className="profile__about-me">Деятельность</p>
                    <button
                        type="button"
                        className="profile__edit-button"
                        onClick={handleEditProfileClick}
                        isOpen={'false'}
                        aria-label="Редактировать профиль"
                    />
                </div>
                <button
                    className="profile__add-button"
                    type="button"
                    onClick={handleAddPlaceClick}

                />
            </section>
            {/* ШАБЛОН ПОПАПА */}
            <PopupWithForm/>
        </main>
    )
}

export default Main;
