import api from "../utils/api";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import {useEffect, useState} from "react";
import ImagePopup from "./ImagePopup";
import EditProfilePopup from "./EditProfilePopup";
import {CurrentUserContext, userContext} from '../contexts/CurrentUserContext';

function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
    const [isDeleteCardPopupOpen, setIsDeleteCardPopupOpen] = useState(false);
    const [selectedCard, setSelectedCard] = useState({})
    const [currentUser, setCurrentUser] = useState()
    const [cards, setCards] = useState([])

    useEffect(() => {
        Promise.all([api.getUserInfo(), api.getCards()])
            .then(([userData, cardsData]) => {
                setCurrentUser(userData)
                setCards(cardsData)
            })
            .catch((err) => {
                console.log(err)
            })
    }, [])

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true)
    }

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true)
    }

    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true)
    }

    function handleDeleteCardClick() {
        setIsDeleteCardPopupOpen(true)
    }

    function closeAllPopups() {
        setIsEditProfilePopupOpen(false)
        setIsAddPlacePopupOpen(false)
        setIsEditAvatarPopupOpen(false)
        setIsDeleteCardPopupOpen(false)
        setSelectedCard({})
    }

    function handleCardClick(selectedCard) {
        setSelectedCard(selectedCard)
    }

    // функция лайка карточки
    function handleCardLike(card) {
        const isLiked = card.likes.some(item => item._id === currentUser._id);
        if (isLiked) {
            api.deleteLike(card._id)
                .then((res) => {
                    setCards((cards) =>
                        cards.map((item) => (item._id === card._id ? res : item)))
                })
                .catch((err) => {
                    alert(err)
                })
        } else {
            api.addLike(card._id)
                .then((res) => {
                    setCards((cards) =>
                        cards.map((item) => (item._id === card._id ? res : item)))
                })
                .catch((err) => {
                    alert(err)
                })
        }
    }

    // функция удаления карточки
    function handleCardDelete (card) {
        api.deleteCard(card._id)
            .then(
                setCards((cards) =>
                    cards.filter((item) => (item._id !== card._id))
                )
            )
    }

    return (
        <CurrentUserContext.Provider value={currentUser}>
            <Header/>
            <Main
                openEditProfile={handleEditProfileClick}
                openAddPlace={handleAddPlaceClick}
                openAvatar={handleEditAvatarClick}
                openDeleteCard={handleDeleteCardClick}
                handleCardClick={handleCardClick}
                handleCardLike={handleCardLike}
                handleCardDelete={handleCardDelete}
                cards={cards}
            />
            {/* IMAGE POPUP */}
            <ImagePopup card={selectedCard} onClose={closeAllPopups}/>

            <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} />

            {/* ADD PLACE */}
            <PopupWithForm
                name={"add-place"}
                title={"Добавить место"}
                isOpen={isAddPlacePopupOpen}
                onClose={closeAllPopups}
                buttonText={"Cохранить"}
            >
                <input type="text"
                       className="popup__input popup__input_name-place"
                       name="name"
                       id="name-place"
                       value=""
                       placeholder="Название"
                       required
                       minLength="2"
                       maxLength="30"
                />
                <span
                    id="name-place-errors"
                    className="popup__error popup__error_visible">
                </span>
                <input type="url"
                       className="popup__input popup__input_link-place"
                       name="link"
                       id="link-place"
                       value=""
                       placeholder="Ссылка на картинку"
                       required
                />
                <span
                    id="link-place-errors"
                    className="popup__error popup__error_visible">
                </span>
            </PopupWithForm>

            {/* EDIT AVATAR */}
            <PopupWithForm
                name={"edit-avatar"}
                title={"Обновить аватар"}
                isOpen={isEditAvatarPopupOpen}
                onClose={closeAllPopups}
                buttonText={"Cохранить"}
            >
                <input type="url"
                       className="popup__input popup__input_link-avatar"
                       name="avatar"
                       id="edit-avatar"
                       value={""}
                       placeholder="Ссылка на картинку"
                       required
                />
                <span
                    id="edit-avatar-errors"
                    className="popup__error popup__error_visible">
                </span>
            </PopupWithForm>

            {/* DELETE CARD */}
            <PopupWithForm
                name={"edit-avatar"}
                title={"Вы уверены?"}
                isOpen={isDeleteCardPopupOpen}
                onClose={closeAllPopups}
                buttonText={"Да"}
            >
            </PopupWithForm>
            <Footer/>
        </CurrentUserContext.Provider>
    )
}

export default App;
